import axios from "axios";
import React, { Component } from "react";

class App extends Component {
    state = {
        selectedFile: null,
        cleanData: [],    // Initialize as empty array
        errorData: [],    // Initialize as empty array
        uploadStatus: "", // To show status messages
    };

    onFileChange = (event) => {
        const file = event.target.files[0];
        console.log("File selected:", file); // Debug file selection
        this.setState({
            selectedFile: file,
            uploadStatus: "", // Reset status when a new file is selected
        });
    };

    onFileUpload = () => {
        if (!this.state.selectedFile) {
            alert("Please select a file before uploading.");
            return;  // Stop further execution
        }

        const formData = new FormData();
        formData.append(
            "myFile",
            this.state.selectedFile,
            this.state.selectedFile.name
        );

        console.log("Uploading file:", this.state.selectedFile.name); // Debug file upload

        axios.post("http://127.0.0.1:5000/view", formData)
            .then(response => {
                console.log("API Response:", response.data); // Log the response data
                this.setState({
                    cleanData: Array.isArray(response.data.clean_data) ? response.data.clean_data : [],
                    errorData: Array.isArray(response.data.error_data) ? response.data.error_data : [],
                    uploadStatus: "Upload successful!"
                });
                console.log("Clean Data State:", this.state.cleanData); // Debug state update
                console.log("Error Data State:", this.state.errorData); // Debug state update
            })
            .catch(error => {
                console.error("There was an error uploading the file!", error); // Debug API error
                this.setState({
                    uploadStatus: "Upload failed. Please try again."
                });
            });
    };

    fileData = () => {
        if (this.state.selectedFile) {
            return (
                <div>
                    <h2>File Details:</h2>
                    <p>File Name: {this.state.selectedFile.name}</p>
                    <p>File Type: {this.state.selectedFile.type}</p>
                    <p>Last Modified: {this.state.selectedFile.lastModifiedDate.toDateString()}</p>
                </div>
            );
        } else {
            return (
                <div>
                    <br />
                    <h4>Choose a file before pressing the Upload button</h4>
                </div>
            );
        }
    };

    renderTable = (data) => {
        if (!Array.isArray(data) || data.length === 0) {
            console.log("No data available for table rendering."); // Debug no data
            return <p>No data available</p>;
        }

        const headers = Object.keys(data[0]);
        console.log("Table headers:", headers); // Debug table headers
        console.log("Table data:", data); // Debug table data

        return (
            <table border="1">
                <thead>
                    <tr>
                        {headers.map(header => (
                            <th key={header}>{header}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {data.map((row, index) => (
                        <tr key={index}>
                            {headers.map(header => (
                                <td key={header}>{row[header]}</td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
        );
    };

    render() {
        console.log("Current State:", this.state); // Debug current state
        return (
            <div>
                <h1>Transaction Processor</h1>
                <h3>File Upload using React!</h3>
                <div>
                    <input
                        accept="text/csv"
                        type="file"
                        onChange={this.onFileChange}
                    />
                    <button onClick={this.onFileUpload}>
                        Upload!
                    </button>
                </div>
                {this.fileData()}
                {this.state.uploadStatus && <p>{this.state.uploadStatus}</p>}
                <h3>Clean Data</h3>
                {this.renderTable(this.state.cleanData)}
                <h3>Error Data</h3>
                {this.renderTable(this.state.errorData)}
            </div>
        );
    }
}

export default App;
